import React from 'react'
import { useEffect,useState } from 'react'
import magic from './CocktailsList.module.css'
import {Link} from 'react-router-dom'

const CocktailsList = () => {

    const [cocktails , setCocktails] = useState([])

    useEffect (() => {
        fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=')
        .then(res => res.json())
        .then(data => { setCocktails(data.drinks)})},[])
    return (
        <div>
            <h1 className={magic.h1x}>Cocktails</h1>
            <hr className={magic.hrr}/>
            <div className={magic.wrapper}>
                {cocktails.map(elem => (
                    <div key={elem.idDrink} className={magic.cocktail}>
                        <Link to={`/coctails/${elem.idDrink}`}><img src={elem.strDrinkThumb} alt={elem.strDrink} /></Link>
                        <button className={magic.coctailName}><span>{elem.strDrink}<img src={favorites} alt='favorites'/></span></button>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default Coctails;
